mapper.select:
select

from livechat_user_relation
where my_id = #{myId,jdbcType=BIGINT}
and others_id = #{othersId,jdbcType=BIGINT}

mapper.delete:
delete
from livechat_user_relation
where my_id = #{myId,jdbcType=BIGINT}
  and others_id = #{othersId,jdbcType=BIGINT}

mapper.update:
update livechat_user_relation
set relation_state                = #{relationState,jdbcType=TINYINT},
    relation_create_datetime      = #{relationCreateDatetime,jdbcType=TIMESTAMP},
    relation_last_update_datetime = #{relationLastUpdateDatetime,jdbcType=TIMESTAMP,typeHandler=com.github.mrag.livechat.enums.typehandler.UserRelationStateTypeHandler}
where my_id = #{myId,jdbcType=BIGINT}
  and others_id = #{othersId,jdbcType=BIGINT}
